mapper.update:
UPDATE tb_salary
	SET
		salary_year_month=#{salaryYearMonth}
		,salary_pay=#{salaryPay}
		, salary_btax_total=#{salaryBtaxTotal}
		, salary_bntax_total=#{salaryBntaxTotal}
		, salary_total=#{salaryTotal}
		, salary_income=#{salaryIncome}
		, salary_income_local=#{salaryIncomeLocal}
		, salary_insu_calc=#{salaryInsuCalc}
		, salary_deduction=#{salaryDeduction}
		, salary_receipts=#{salaryReceipts}
		, salary_pay_date=#{salaryPayDate}
	WHERE salary_code=#{salaryCode}

mapper.insert:
INSERT INTO tb_salary
	(salary_code
	, store_info_code
	, member_id
	, salary_year_month
	, salary_pay
	, salary_btax_total
	, salary_bntax_total
	, salary_total
	, salary_income
	, salary_income_local
	, salary_insu_calc
	, salary_deduction
	, salary_receipts
	, salary_deadline_check
	, salary_pay_date
	, salary_reg_date)
VALUES 
	(sf_new_salary_code()
	, #{storeInfoCode}
	, #{memberId}
	, #{salaryYearMonth}
	, #{salaryPay}
	, #{salaryBtaxTotal}
	, #{salaryBntaxTotal}
	, #{salaryTotal}
	, #{salaryIncome}
	, #{salaryIncomeLocal}
	, #{salaryInsuCalc}
	, #{salaryDeduction}
	, #{salaryReceipts}
	, 'x'
	, #{salaryPayDate}
	, NOW())
